    <div>
    <div className="new-expense">
    {!isToggle && <button onClick={toggleSwitch}>Add a New Expense</button>}

    {isToggle && <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />}

    </div>
        </div>
  );
};

