import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [enteredDateFilter, setEnteredDateFilter] = useState("");

  const dateFilterHandler = (event) => {
    setEnteredDateFilter(event.target.value);
  };

  const expensesFilterData = {
    date: enteredDateFilter
  }
  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={dateFilterHandler}>
            2022
          </option>
          <option value="2021">2021</option>
          <option value="2020">2020</option>
          <option value="2019">2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
