const Menu = (props) => {
    const [isToggle, setIsToggle] = useState(false);
  const clickHandler = () => {
   setIsToggle(!isToggle)

  };
props.setIsToggle(true)
  return (
    <div className="Add-expense">
      <h1>Menu</h1>
      <div className="Add-expense__actions">
        <button onClick={clickHandler} type="submit">
          Add A New Expense
        </button>
      </div>
    </div>
  );
};

export default Menu;
